def methode(filePath, map){

	try {
    		FileInputStream fis = new FileInputStream(filePath)
    		Workbook workbook = new XSSFWorkbook(fis)
    		XSSFSheet sheet = workbook.getSheetAt(0)
    		fis.close()

    		for (int i = 0; i < sheet.getPhysicalNumberOfRows(); i++) {
     		row = sheet.getRow(i);

     		if (row.getCell(0).getStringCellValue() == map.ref){
          		for(int mp = 0; mp < map.size(); mp++){
               		def Key = map.keySet().toList()[mp]
          			if (fonction(sheet.getRow(0),Key) != null){
          				sheet.getRow(i).createCell(fonction(sheet.getRow(0),Key)).setCellValue(map.get(Key))
          			}
          		}
          	}
          }

		FileOutputStream fos = new FileOutputStream(filePath)
    		workbook.write(fos)
		fos.close()
   		workbook.close()
   		log.info( "Excel modifie avec succs!")

		} catch (IOException e) {
    			e.printStackTrace()
    			log.info( "Une erreur s'est produite lors de la modification du fichier Excel.")
		}}


def fonction(row,RequestName){

	for (int j = 0; j < row.getPhysicalNumberOfCells(); j++) {

		Cell cell = row.getCell(j)
		if (cell.getStringCellValue() == RequestName){
			def indexc = cell.columnIndex
			//log.info("index"+indexc)
			return indexc
			}
	}
	}
	def RequestMethod (filePath,Ref,RequestName){
	def req = "Web_Services"
	def index = 0
	File file = new File(filePath)
	FileInputStream fis = new FileInputStream(file)
	XSSFWorkbook workbook = new XSSFWorkbook(fis)
	XSSFSheet sheet = workbook.getSheetAt(1)
	def mergedRegions = sheet.getMergedRegions()
	log.info(""+mergedRegions.isEmpty())
	Row row = sheet.getRow(0)
	index = fonction(row,req)
	for (int i = 1; i < sheet.getPhysicalNumberOfRows(); i++) {
     	row = sheet.getRow(i)
		cell = row.getCell(0)
		if (cell.getStringCellValue().toString() == Ref){
			if (! mergedRegions.isEmpty() ){
				for (region in mergedRegions) {
    					if (region.containsRow(i)) {
    						for(r in region.firstRow .. region.getLastRow() ){
    							if(sheet.getRow(r).getCell(index).getStringCellValue() == RequestName ){
    								def val = sheet.getRow(r).getCell(index + 1).getStringCellValue()
    								log.info("SUCCES")
    								return val
    							}
    						}
    					}
				}

			}
			if(row.getCell(index).getStringCellValue() == RequestName){
    				def val = row.getCell(index+1).getStringCellValue()
    				log.info("SUCCES if ")
    				return val
			}
		}
	}
}
